mapper.select:
		select 	*
		from 	vacation
		where 	emp_num = #{ emp_num }

mapper.update:
		update vacation
		set 
		va_stock = (
        (SELECT
            va_stock
        FROM vacation
        where emp_num = #{ calendar_emp_num }
        ) - (

			0.5
                    )
        )
		where emp_num = #{ calendar_emp_num }

UPDATE approve_progress
  SET prg_auth1 = 1
  where app_num = #{ app_num }

UPDATE approve_progress
  SET prg_auth2 = 1
  where app_num = #{ app_num }

UPDATE approve_progress
  SET prg_auth3 = 1
  where app_num = #{app_num}

mapper.select:
    select  sum(prg_auth1 + prg_auth2 + prg_auth3)
	from 	approve_progress
	where 	app_num = #{app_num}

mapper.update:
UPDATE approve_progress
  SET PRG_STATUS = '결재승인'
  where app_num = #{ app_num }

UPDATE approve
  SET app_prg = '결재승인'
  where app_num = #{ app_num }

    	update approve
