mapper.select:

select 	*
from 	vacation
where 	emp_num = #{ emp_num }

mapper.update:
		update vacation
		set 
		va_stock = (
        (SELECT
            va_stock
        FROM vacation
        where emp_num = #{ calendar_emp_num }
        ) - (
        SELECT (TO_DATE(#{calendar_end, jdbcType=VARCHAR}, 'YYYY-MM-DD') -  TO_DATE(#{calendar_start, jdbcType=VARCHAR}, 'YYYY-MM-DD'))+1 FROM DUAL
            )
        )
		where emp_num = #{ calendar_emp_num }
