into chat_member values(#{room_num},#{item}, LOG_NUM)
select LOG_NUM from CHAT_LOG cl_sort, (select max(SEND_DATE) SEND_DATE from CHAT_LOG group by ROOM_NUM) max_date where cl_sort.SEND_DATE = max_date.SEND_DATE and cl_sort.room_num = #{room_num}

